import React from 'react';
import PropTypes from 'prop-types';
import './searchPage.scss';
import { inject, observer } from "mobx-react";

import SearchList from '../components/SearchList';
import SearchListItem from '../components/SearchListItem';
import PageLayout from '../components/PageLayout';

class SearchPage extends React.Component {

    componentDidMount() {
        // this.props.appStore.handleSearch('surf paddle');
    }

    handleSearch = (event) => {
        // this.props.appStore.handleSearch('surf paddle');
        this.props.appStore.handleSearch(event.currentTarget.value);
    }

    render() {
        // console.log(toJS(this.props.appStore.searchResults));
        return (
            <PageLayout>
                <div className="app">
                    <input onChange={this.handleSearch} />

                    {this.props.appStore.searchResults.map((result, i) =>(
                        <SearchList key={i}>
                            <SearchListItem data={result} />
                        </SearchList>
                    ))}
                </div>
            </PageLayout>

        );

    }
}

SearchPage.propTypes = {
    appStore: PropTypes.object
}

export default inject(['appStore'])(observer(SearchPage));
