    // @action
    handleSearch = (query) => {
        return new Promise((resolve, reject) => {
            fetchData(query)
                .then((response) => {
                    console.log('resp', response);
                    resolve(response);
                })
                .catch((e) => {
                    reject(e);
                });
        });
    }

}

export default decorate(AppStore, {
    handleSearch: action
});
