import React from 'react';
import PropTypes from 'prop-types';
import './repoPage.scss';
import { inject, observer } from "mobx-react";
import { toJS } from 'mobx';
import PageLayout from '../components/PageLayout';
import moment from 'moment';
import UserInteraction from '../components/UserInteraction';
import Eye from '../components/Eye';
import Star from '../components/Star';

class RepoPage extends React.Component {

    componentDidMount() {
        const params = this.props.match.params;
        this.props.appStore.fetchDataByRepoName(params.name, params.repo);
    }

    render() {
        console.log('props', toJS(this.props.appStore.repoData));
        const data = this.props.appStore && this.props.appStore.repoData;
        const owner = data && data.owner;
        return (
            <PageLayout>
                <div className="repoPage">
                    <div className="repoPage__ownerDetails">
                        <div className="repoPage__ownerImageOuter">
                            <img className="repoPage__ownerImage" src={owner && owner.avatar_url} />
                        </div>
                        <div className="repoPage__ownerName">
                            {owner && owner.login}
                        </div>
                        <a className="repoPage__ownerLink" href={owner && owner.html_url}>
                            {owner && owner.html_url}
                        </a>
                    </div>
                    <div className="repoPage__repoDetails">
                        <div className="repoPage__repoName">
                            {data.name}
                        </div>
                        <div className="repoPage__repoDescription">
                            {data.description}
                        </div>
                        <div className="repoPage__repo">
                            <UserInteraction
                                image={<Star />}
                                value={data.stargazers_count}
                            />
                        </div>
                        <div className="repoPage__repo">
                            <UserInteraction image={<Eye />} value={data.watchers_count} />
                        </div>
                        <div className="repoPage__repo">{data.language}</div>
                        <div className="repoPage__repo">Created: {moment(data.created_at).format("DD MMM YYYY HH:mm")}</div>
                        <div className="repoPage__repo">Updated: {moment(data.updated_at).format("DD MMM YYYY HH:mm")}</div>
                    </div>
                </div>
            </PageLayout>
        );
    }
}


RepoPage.propTypes = {
    appStore: PropTypes.object
}

export default inject(['appStore'])(observer(RepoPage));
